INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Mass Effect Trilogy', 4.8, 2012, 'RPG, Tiro', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/1.png', 'Trilogia épica de RPG de ficção científica onde você lidera a luta contra uma ameaça galáctica.', 'Assuma o papel do Comandante Shepard e lidere uma equipe diversa para salvar a galáxia de uma antiga raça de máquinas. Suas decisões impactam profundamente a narrativa e o destino de civilizações inteiras.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Red Dead Redemption 2', 4.7, 2018, 'RPG, Aventura', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/2.png', 'Aventura épica no Velho Oeste, acompanhando o fora-da-lei Arthur Morgan em sua luta pela sobrevivência.', 'Ambientado no final do século XIX, siga Arthur Morgan, membro da gangue Van der Linde, enquanto enfrenta mudanças na América e lida com lealdade e moralidade em um mundo implacável.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Witcher 3: Wild Hunt', 4.7, 2014, 'RPG, Aventura', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/3.png', 'RPG de mundo aberto onde Geralt de Rivia busca por sua filha adotiva em meio a uma guerra devastadora.', 'Como Geralt, um caçador de monstros profissional, explore um vasto mundo repleto de cidades, montanhas e mares, enfrentando criaturas perigosas e tomando decisões que moldam a história e o destino dos personagens.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sekiro: Shadows Die Twice', 3.8, 2019, 'Ação, Aventura', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/4.png', 'Ação intensa no Japão feudal, onde um guerreiro busca resgatar seu mestre e restaurar sua honra.', 'Controle um shinobi desonrado armado com uma prótese e habilidades ninja, enfrentando inimigos formidáveis em combates desafiadores, utilizando furtividade, ganchos e técnicas de espada para superar obstáculos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ghost of Tsushima', 4.6, 2020, 'Ação, Aventura', 'Playstation', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/5.png', 'Explore a ilha de Tsushima durante a invasão mongol, decidindo entre honra samurai e táticas furtivas.', 'Jogue como Jin Sakai, um samurai que deve abandonar tradições para proteger seu lar, explorando um mundo aberto rico, participando de combates com katana e utilizando furtividade para derrotar inimigos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Super Mario World', 4.7, 1990, 'Plataforma', 'Super Nintendo, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/6.png', 'Aventura clássica onde Mario e Luigi exploram a Dinosaur Land para salvar a Princesa Toadstool.', 'Introduzindo Yoshi, os irmãos Mario enfrentam os Koopalings e Bowser em diversos níveis, utilizando power-ups e habilidades para superar obstáculos e inimigos em um mundo vibrante e diversificado.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Hollow Knight', 4.6, 2017, 'Ação, Aventura', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/7.png', 'Explore um vasto reino subterrâneo em ruínas, enfrentando criaturas corrompidas e descobrindo segredos.', 'Como um cavaleiro silencioso, viaje por cavernas sinuosas e cidades antigas, aprimorando habilidades e combatendo inimigos desafiadores em um mundo meticulosamente desenhado à mão.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ori and the Blind Forest', 4.0, 2015, 'Plataforma', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/8.png', 'Jornada emocional de um jovem órfão destinado a feitos heroicos em uma floresta moribunda.', 'Controle Ori em uma aventura de plataforma visualmente deslumbrante, enfrentando desafios e inimigos, enquanto desvenda uma história comovente sobre amor, sacrifício e esperança.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Cuphead', 4.6, 2017, 'Plataforma', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/9.png', 'Jogo de ação e tiro inspirado em desenhos animados dos anos 1930, focado em batalhas contra chefes.', 'Assuma o papel de Cuphead ou Mugman, enfrentando uma série de chefes únicos em níveis desafiadores, com visual e trilha sonora autênticos da era dourada da animação.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sonic CD', 4.0, 1993, 'Plataforma', 'Sega CD, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/10.png', 'Aventura de plataforma onde Sonic viaja no tempo para salvar o Little Planet do Dr. Robotnik.', 'Utilize mecânicas de viagem no tempo para explorar versões passadas, presentes e futuras das fases, enfrentando o vilão e seu robô sósia, Metal Sonic, em uma missão para resgatar Amy Rose.');
